mapper.select:
select count(*)
from member
where status='Y' and username=#{username}

select count(*)
from member
where status='Y' and NICKNAME=#{nickname}

mapper.update:
update Member
set nickname = #{nickname}
where userNo=#{userNo}

update Member
set userpwd = #{password}
where username = #{username}

mapper.select:
select username
from member
where status='Y' and email=#{email}

mapper.update:
