mapper.select:
select MY_USERNO, SKINPATH, MAINTITLE, PROFILEPATH, SIDECONTENT
from mypage
where MY_USERNO = #{myUserNo}

select count(*)
from mypage
where MY_USERNO = #{myUserNo}

mapper.update:
update mypage
set skinPath = #{skinPath}
where my_userno = #{userNo}

update mypage
set profilePath = #{profilePath}
where my_userNo = #{userNo}

update mypage
set maintitle = #{mainTitle}
where my_userno = #{userNo}

update mypage
set sidecontent = #{sideContent}
where my_userno = #{userNo}
